import React,{Component} from "react";
import { BrowserRouter as Router, Route } from 'react-router-dom'
import { Routes } from "react-router-dom"
import withNavigation from './WithNavigation.jsx'
import withParams from './WithParams.jsx'
import './bootstrap.css';
import './bootstrap.min.css.map'
// import AuthenticationService from "./AuthenticationService.js";
import AuthenticatedRoute from "./AuthenticatedRoute.jsx"
import LoginComponent from "./LoginComponent.jsx";
import ListTodosComponent from "./ListTodosComponent.jsx";
import HeaderComponent from "./HeaderComponent.jsx";
import LogoutComponent from "./LogoutComponent.jsx";
import ErrorComponent from "./ErrorComponent.jsx";
import FooterComponent from "./FooterComponent.jsx";
import WelcomeComponent from "./WelcomeComponent.jsx";
import TodoComponent from "./TodoComponent"
class TodoApp extends Component{
    render(){
        const LoginComponentWithNavigation = withNavigation(LoginComponent);
        const ListTodoComponentWithNavigation = withNavigation(ListTodosComponent)

        const WelcomeComponentWithParams = withParams(WelcomeComponent);

        const TodoComponentWithParamsAndNavigation = withParams(withNavigation(TodoComponent));

        const HeaderComponentWithNavigation = withNavigation(HeaderComponent);
        return (
            <div className="TodoApp">
                <Router>
                    <HeaderComponentWithNavigation/>
                    <Routes>
                        {/* in Router you can only have one child element */}
                        <Route path="/" element={<LoginComponentWithNavigation/>}/>
                        <Route path="/login" element={<LoginComponentWithNavigation/>}/>
                        <Route path="/welcome/:name" element={
                            <AuthenticatedRoute>
                                <WelcomeComponentWithParams />
                            </AuthenticatedRoute>} /> 
                        {/* route has priority the higher the more advanced  */}
                        <Route path="/todos/:id" element={ 
                            <AuthenticatedRoute>
                                <TodoComponentWithParamsAndNavigation />
                            </AuthenticatedRoute>} />
                        <Route path="/todos" element={
                            <AuthenticatedRoute>
                                <ListTodoComponentWithNavigation/>
                            </AuthenticatedRoute>} />

                        <Route path="/logout" element={
                            <AuthenticatedRoute>
                                <LogoutComponent />
                            </AuthenticatedRoute>} />
                        <Route path="*" element={<ErrorComponent />} />
                    </Routes>
                    <FooterComponent/>
                </Router>
                {/* <LoginComponent></LoginComponent> */}
            </div>
        )
    }
}










